import { spawn, spawnSync } from 'child_process';
import { app, BrowserWindow, Menu } from 'electron';
import { existsSync } from 'fs';
import { platform } from 'os';
import { join } from 'path';
import { log } from './main';

let packageManager: 'pnpm' | 'yarn' | 'npm' | null = null;

export function getPackageManager(): 'npm' | 'pnpm' | 'yarn' {
  if (packageManager) return packageManager;

  if (!spawnSync('pnpm', ['-v'], { shell: platform() === 'win32' }).error)
    return (packageManager = 'pnpm');
  if (!spawnSync('yarn', ['-v'], { shell: platform() === 'win32' }).error)
    return (packageManager = 'yarn');
  if (!spawnSync('npm', ['-v'], { shell: platform() === 'win32' }).error)
    return (packageManager = 'npm');
  throw new Error('No package manager installed');
}

function getWindowContents(script: string) {
  return `<!DOCTYPE html>
  <html lang="en">
    <head>
      <meta charset="UTF-8" />
      <meta http-equiv="X-UA-Compatible" content="IE=edge" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>Document</title>
      <style>
        :root {
          --color-primary: #059669;
          --color-secondary: #10b981;
          --color-bg: #171717;
          --color-gray: #a3a3a3;
        }
  
        body {
          background-color: var(--color-bg);
          color: #fff;
          margin: 1rem;
          font-family: sans-serif;
          width: calc(100vw - 2rem);
          height: calc(100vh - 2rem);
        }
  
        div {
          display: flex;
          flex-direction: row;
          gap: 2rem;
          width: 100%;
          height: 100%;
          justify-content: center;
          align-items: center;
        }
  
        img {
          width: 5rem;
          height: 5rem;
        }
  
        p {
          font-size: xx-large;
        }
      </style>
    </head>
    <body>
      <div>
          <img
            src="data:image/png;base64,"
          />
          <p>
              Installing ${script}
          </p>
      </div>
    </body>
  </html>
  `;
}

function installPackage(
  packagemanager: 'npm' | 'yarn' | 'pnpm',
  pkg: string,
  dir: string
): Promise<void> {
  return new Promise((res) => {
    const spawned = spawn(
      packagemanager,
      [packagemanager === 'yarn' ? 'add' : 'install', pkg],
      {
        cwd: dir,
        shell: platform() === 'win32',
      }
    );

    spawned.stderr.pipe(process.stderr);
    spawned.stderr.pipe(process.stdout);

    spawned.on('error', (err) => {
      console.error(err);
      res();
    });
    spawned.on('close', res);
    spawned.on('exit', res);
    spawned.on('disconnect', res);
  });
}

export default async function installRequiredPackages(
  dir: string,
  packages: string[]
) {
  let window: BrowserWindow | null = null;

  const manager = getPackageManager();
  for (const p of packages) {
    if (!existsSync(join(dir, 'node_modules', p))) {
      if (!window) {
        window = new BrowserWindow({
          width: 600,
          height: 150,
          frame: false,
          autoHideMenuBar: true,
        });

        window.setMenu(
          Menu.buildFromTemplate([
            {
              label: 'Window',
              submenu: [
                {
                  label: 'Close',
                  accelerator: 'Escape',
                  role: 'close',
                },
                {
                  label: 'Toggle Devtools',
                  accelerator: (function () {
                    if (process.platform === 'darwin') return 'Alt+Command+I';
                    else return 'Ctrl+Shift+I';
                  })(),
                  click: function (item, focusedWindow) {
                    if (focusedWindow)
                      focusedWindow.webContents.toggleDevTools();
                  },
                },
              ],
            },
          ])
        );
      }

      window.loadURL(
        'data:text/html;base64,' +
          Buffer.from(getWindowContents(p)).toString('base64')
      );
      window.show();

      log('info', 'package-manager', 'Installing %s', p);
      await installPackage(manager, p, dir);
      log('info', 'package-manager', 'Installed %s', p);
    }
  }

  try {
    if (window) (window as BrowserWindow).close();
  } catch {}
}
